import React, { useState, useEffect, useMemo } from 'react';
import Header from '../components/Header';
import Message from '../components/Message';
import GetStartedButton from '../components/GetStartedButton';
import ImageSection from '../components/ImageSection';
import './Home.css';

function Home() {
    const messages = useMemo(() => [
        { src: require('../assets/msg1.png') },
        { src: require('../assets/msg2.png') },
        { src: require('../assets/msg3.png') },
        { src: require('../assets/msg4.png') },
      ], []);
      
  const [visibleMessages, setVisibleMessages] = useState([]);

  useEffect(() => {
    let index = 0;

    const interval = setInterval(() => {
      if (index < messages.length) {
        setVisibleMessages((prevMessages) => [...prevMessages, messages[index]]);
        index++;
      } else {
        clearInterval(interval);
      }
    }, 1000);

    return () => clearInterval(interval);
  }, [messages]);

  return (
    <div className="home">
      <Header />
      <div className="messages">
        {visibleMessages.map((message, index) => (
          message && message.src ? ( // Verificação adicional
            <Message key={index} src={message.src} />
          ) : null
        ))}
      </div>
      <GetStartedButton />
      <ImageSection />
    </div>
  );
}

export default Home;
