import React, { useState, useEffect } from 'react';
import Header from '../components/Header';
import Message from '../components/Message';
import GetStartedButton from '../components/GetStartedButton';
import ImageSection from '../components/ImageSection';
import './Home.css';

function Home() {
 
  const messages = [
    { src: './assets/msg1.png' }, 
    { src: '/assets/msg2.png' },
    { src: '/assets/msg3.png' },
    { src: '/assets/msg4.png' },
  ];

  // Estado para gerenciar as imagens visíveis
  const [visibleMessages, setVisibleMessages] = useState([]);

  useEffect(() => {
    let index = 0;

    // Função para adicionar imagens com atraso
    const interval = setInterval(() => {
      if (index < messages.length) {
        setVisibleMessages((prevMessages) => [...prevMessages, messages[index]]);
        index++;
      } else {
        clearInterval(interval); // Para o intervalo quando todas as mensagens forem exibidas
      }
    }, 1000); // Adiciona uma imagem a cada 1 segundo

    return () => clearInterval(interval); // Limpeza do intervalo ao desmontar o componente
  }, [messages]);

  return (
    <div className="home">
      <Header />
      <div className="messages">
        {visibleMessages.map((message, index) => (
          <Message key={index} src={message.src} isBold={message.isBold} />
        ))}
      </div>
      <GetStartedButton />
      <ImageSection />
    </div>
  );
}

export default Home;
